    export function handleCreditClick(host:any, e:any,args:any,chart:string){
        /* TODO, this will be refactored in the new free visual */
        let targets = {
            piechart: "zoomcharts.com/powerbi-custom-visuals/donut-chart",
            timechart: "zoomcharts.com/powerbi-custom-visuals/timeline-chart",
            netchart: "zoomcharts.com/powerbi-custom-visuals/network-chart",
            facetchart: "zoomcharts.com/powerbi-custom-visuals/column-chart"
        }

        if (args.clickCredits){
            openURL(host, "https://" + targets[chart]);
            e.preventDefault();
            return false;
        }
    }


    export function updateScale(options: VisualUpdateOptions, chart) {
        let tempViewport: any = options.viewport;
        let tmpScale = 0;
        let scale: any = null;
        if (isDebugVisual){
            console.log("Viewport scale & pixel ratio", tempViewport.scale, window.devicePixelRatio);
        }
        if (tempViewport.scale) {
            tmpScale = tempViewport.scale;
            if (tmpScale == 1) {
                scale = true;
            } else if (tmpScale > 0 && tmpScale < 1) {
                scale = tmpScale * 2;
            } else if (tmpScale > 1) {
                if (window.devicePixelRatio) {
                    scale = tmpScale * window.devicePixelRatio;
                } else if (window.window.devicePixelRatio) {
                    scale = tmpScale * window.window.devicePixelRatio;
                } else {
                    scale = tmpScale * 1;
                }
            }
        }

        if (scale) {
            chart.replaceSettings({
                advanced: {
                    highDPI: 2
                }
            });
        }
        if (isDebugVisual){
            console.log("Current visual scale", scale);
        }
        return scale;
    }

    export function updateSize(visual, viewport, newViewMode?:any) {
        let _thisVisual: any = visual;
        let viewportHeight: number = Math.round(viewport.height);
        let viewportWidth: number = Math.round(viewport.width);

        let scale: number = 1;
        let posTop: number = 0;
        let posLeft: number = 0;
        let posType: string = "fixed"; // don't use any other 'position' property
        let transform: string = "scale3d(1, 1, 1)";
        let nh: number = viewportHeight;
        let nw: number = viewportWidth;
        let chartContainer: any = _thisVisual.target.getElementsByClassName("chart-container")[0];

        if (!_thisVisual.currentScale) {
            _thisVisual.currentScale = 1;
        }

        if (typeof(viewport.scale) != "undefined") {
            scale = viewport.scale;
        } else {
            scale = _thisVisual.currentScale;
        }

        visualCurrentMode = newViewMode;

        if (scale) {
            scale = _thisVisual.currentScale = scale;
        } else {
            scale = _thisVisual.currentScale;
        }

        if (isDebugVisual) {
            console.log("Scale, viewport width, viewport height", scale, viewportWidth, viewportHeight);
        }

        if (scale > 1) {
            nh = Math.round(viewportHeight * scale);
            nw = Math.round(viewportWidth * scale);
            posTop = -Math.round((viewportHeight - viewportHeight * 1 / scale) / 2 * scale);
            posLeft = -Math.round((viewportWidth - viewportWidth * 1 / scale) / 2 * scale);
            transform = "scale3d(" + (1 / scale) + "," + (1 / scale) + ",1)";
        }

        // workaround for width and height on outer div, i.e., "#sandbox-host",
        // workaround consists of 'position', 'top' and 'left' css properties
        _thisVisual.target.style.position = posType;
        _thisVisual.target.style.top = posTop + "px";
        _thisVisual.target.style.left = posLeft + "px";
        _thisVisual.target.style.transform = transform;

        // outer and inner div's need to be set with css properties 'minHeight' and 'minWidth'
        _thisVisual.target.style.minHeight = chartContainer.style.height = (nh + "px");
        _thisVisual.target.style.minWidth = (nw + "px");

        if (isMobileTabletDevice()) {
            // note! both 'minHeight' and 'height' should be set for 'chart-container'
            chartContainer.style.minHeight = chartContainer.style.height = ((nh - 6) + "px");
        }

        outerSize = [nw, nh];

        this.viewMode = newViewMode;
        _thisVisual.chart.updateSize();

        updateOverlays();
    }

    export function updateOverlays(dom?:any){
        if (messageOverlays){
            let nw = outerSize[0];
            let nh = outerSize[1];

            if (isDebugVisual){
                console.log("Updating overlays", messageOverlays);
            }
            for (var x = 0; x < messageOverlays.length; x++){
                let o = messageOverlays[x];
                let elem = o[0];
                if (dom && dom != elem) continue;
                let callback = o[1];
                if (callback){
                    let n = callback(elem, nw, nh);
                } else {
                    elem.style.height = nh + "px";
                    elem.style.width = nw + "px";
                }
            }
        }
    }

