    export function clickify(elements:Array<HTMLElement>, host){
        for (let x = 0; x < elements.length; x++){
            elements[x].addEventListener("click", (e:any)=>{
                e.preventDefault();
                let href = e.currentTarget.getAttribute("href");
                if (e.currentTarget != e.srcElement){
                    let stars = e.target.getAttribute("stars");
                    href += stars;
                }


                let r = new RegExp("pbi/", "g");
                href = href.replace(r, "pbi/" + ((!visualCurrentMode)?"view":"edit") +"/");
                openURL(host, href);
                return false;
            });
        }
    }

